mapper.delete:
DELETE
FROM attachment
WHERE name = #{name} and year = #{year}

mapper.update:
UPDATE attachment
SET file = #{file}
WHERE id = #{id}

mapper.select:
SELECT *
FROM attachment
Where name LIKE #{name} and year = #{year} and type = #{type}
Limit #{start},#{perpage}

SELECT *
FROM attachment
Where name LIKE #{name} and type = #{type}
Limit #{start},#{perpage}

SELECT COUNT(*)
FROM attachment
Where name LIKE #{name} and type = #{type}
