mapper.delete:
DELETE
FROM point
WHERE name = #{name} and year = #{year}

mapper.update:
UPDATE point
SET point = #{point}
WHERE id = #{id}

mapper.select:
SELECT *
FROM point
Where name = #{name} and mid = #{mid} and year = #{year}

SELECT *
FROM point
Where type = #{type}  and year = #{year}

SELECT *
FROM point
Where mid = #{mid} and year = #{year}

SELECT *
FROM point
Where mid = #{mid} and year = #{year} and type = #{type}

SELECT *
FROM point
Where name = #{name} and year = #{year} and type = #{type}

SELECT COUNT(*)
FROM point
Where name = #{name}
